import {getTransactionInfo} from "@/api/blockchain/blockchain.js";

const recordValue = ref()
const page = ref(1)
const transactionHash = ref()
const transaction = ref({})
const transaction_detail = ref({})
const transaction_receipt = ref({})
const title = ref({})
const record = ref({})
const trace = ref()
const total = ref(0)
const data = reactive({
  queryParams: {
    pageNum: 1,
    pageSize: 10
  },
  tableData: []
})
const {tableData,queryParams,option} = toRefs(data)

watch(record,(val,old) => {
  title.value = val
  selectTransactionHashAndBlockNumber({raiseId: title.value.raiseId}).then(res => {
    transaction.value = res.data
    console.log(transaction.value)
  })
})

function handleSearchTransactionByHash(){
  getTransactionInfo({hash: transactionHash.value}).then(res => {
    transaction_detail.value = res.transaction
    transaction_receipt.value = res.transactionReceipt
  })
}
onMounted(() => {
  selectUserDonationRaiseFundRecord().then(res => {
    total.value = res.total
    tableData.value = res.rows

    title.value = res.rows[0]
  })
})

